function thirteen(strNumber) {
  if (strNumber.includes('0')) {
    return 0
  }
  let product = 1, length = strNumber.length
  for (let i = 0; i < length; i++) {
    product *= parseInt(strNumber[i])
  }
  return product
}

function problem8(number1000) {
  let startIndex = 0, endIndex = 13
  const length = number1000.length
  let biggestProduct = 0, tempProduct, current13, biggest13

  while (endIndex <= length) {
    current13 = number1000.slice(startIndex, endIndex)
    tempProduct = thirteen(current13)
    if (tempProduct > biggestProduct) {
      biggestProduct = tempProduct
      biggest13 = current13
    }
    startIndex += 1
    endIndex += 1
  }
  return biggestProduct
}

